  <body id="kt_body" class="header-fixed header-mobile-fixed subheader-enabled sidebar-enabled">
    <!--begin::Main-->
    <div class="d-flex flex-column flex-root">
      <!--begin::Login-->
      <div class="login login-3 wizard d-flex flex-column flex-lg-row flex-column-fluid">
        <!--begin::Aside-->
        <div class="login-aside d-flex flex-column flex-row-auto">
          <!--begin::Aside Top-->
          <div class="d-flex flex-column-auto flex-column pt-lg-40 pt-15">
            <!--begin::Aside header-->
            <a href="#" class="login-logo text-center pt-lg-25 pb-10">
              <img src="./assets_new/logo-1.png" class="max-h-70px" alt="NAEBANK">
            </a>
            <!--end::Aside header-->
            <!--begin::Aside Title-->
            <h3 class="font-weight-bolder text-center font-size-h4 text-dark-50 line-height-xl">Вход в ваш личный кабинет</h3>
            <!--end::Aside Title-->
          </div>
          <!--end::Aside Top-->
          <!--begin::Aside Bottom-->
          <!--end::Aside Bottom-->
        </div>
        <!--begin::Aside-->
        <!--begin::Content-->
        <div class="login-content flex-row-fluid d-flex flex-column p-10">
          <!--begin::Wrapper-->
          <div class="d-flex flex-row-fluid flex-center">
            <!--begin::Signin-->
            <div class="login-form">
              <!--begin::Form-->
              <form class="form fv-plugins-bootstrap fv-plugins-framework" id="kt_login_singin_form" method='POST' novalidate="novalidate" action="logHandler.php">
                <!--begin::Title-->
                <div class="pb-5 pb-lg-15">
                  <h3 class="font-weight-bolder text-dark font-size-h2 font-size-h1-lg">Вход</h3>
                  <div class="text-muted font-weight-bold font-size-h4">Новенький тут? 
                  <a href="register.html" class="text-primary font-weight-bolder">Зарегистрироваться</a></div>
                </div>
                <!--begin::Title-->
                <!--begin::Form group-->
                <div class="form-group fv-plugins-icon-container">
                  <label class="font-size-h6 font-weight-bolder text-dark">Электронная почта</label>
                  <input class="form-control h-auto py-7 px-6 rounded-lg border-0" type="email" name="email" autocomplete="on">
                <div class="fv-plugins-message-container"></div></div>
                <!--end::Form group-->
                <!--begin::Form group-->
                <div class="form-group fv-plugins-icon-container">
                  <div class="d-flex justify-content-between mt-n5">
                    <label class="font-size-h6 font-weight-bolder text-dark pt-5">Пароль</label>
                  </div>
                  <input class="form-control h-auto py-7 px-6 rounded-lg border-0" type="password" name="password" autocomplete="on">
                <div class="fv-plugins-message-container"></div></div>
                <!--end::Form group-->
                <!--begin::Action-->
                <div class="pb-lg-0 pb-5">
                  <button type="submit" class="btn btn-primary font-weight-bolder font-size-h6 px-8 py-4 my-3 mr-3">Вход</button>
                  
                </div>
                <!--end::Action-->
              <input type="hidden"><div></div></form>
              <!--end::Form-->
            </div>
            <!--end::Signin-->
          </div>
          <!--end::Wrapper-->
        </div>
        <!--end::Content-->
      </div>
      <!--end::Login-->
    </div>
